import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { eventAPI } from '../services/api';
import { useAuth } from '../context/AuthContext';
import { getImageUrl, handleImageError } from '../utils/imageUtils';
import '../styles/EventDetail.css';

export const EventDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user, isAuthenticated } = useAuth();
  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [isRegistered, setIsRegistered] = useState(false);
  const [registering, setRegistering] = useState(false);

  useEffect(() => {
    fetchEvent();
  }, [id]);

  const fetchEvent = async () => {
    try {
      const response = await eventAPI.getEventById(id);
      setEvent(response.data.event);

      if (isAuthenticated) {
        checkRegistration(response.data.event);
      }
    } catch (err) {
      setError('Event not found');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const checkRegistration = (eventData) => {
    setIsRegistered(eventData.registeredUsers?.includes(user?._id));
  };

  const handleRegister = async () => {
    if (!isAuthenticated) {
      navigate('/signin');
      return;
    }

    try {
      setRegistering(true);
      await eventAPI.registerForEvent(id);
      setIsRegistered(true);
      alert('Successfully registered for event!');
    } catch (err) {
      setError(err.response?.data?.message || 'Registration failed');
    } finally {
      setRegistering(false);
    }
  };

  if (loading) return <div className="loading">Loading event details...</div>;
  if (!event) return <div className="error-message">Event not found</div>;

  return (
    <div className="event-detail">
      <button className="back-btn" onClick={() => navigate(-1)}>← Back</button>

      <div className="event-detail-container">
        {event.posterUrl && (
          <div className="event-poster-large">
            <img
              src={getImageUrl(event.posterUrl)}
              alt={event.title}
              onError={handleImageError}
            />
          </div>
        )}

        <div className="event-info">
          <span className={`event-sector-badge sector-${event.sector}`}>
            {event.sector.toUpperCase()}
          </span>

          <h1>{event.title}</h1>

          <div className="event-meta">
            <div className="meta-item">
              <strong>Date:</strong>
              <p>{new Date(event.eventDate).toLocaleDateString()}</p>
            </div>
            <div className="meta-item">
              <strong>Time:</strong>
              <p>{event.eventTime || 'TBD'}</p>
            </div>
            <div className="meta-item">
              <strong>Location:</strong>
              <p>
                {event.location?.address}, {event.location?.city}, {event.location?.state}
              </p>
            </div>
          </div>

          <div className="event-description">
            <h3>Event Details</h3>
            <p>{event.description}</p>
          </div>

          {/* Organizer Section */}
          <div className="organizer-section">
            <h3>Organizer</h3>
            <div className="organizer-info">
              {event.organizerDetails?.logo && (
                <img src={event.organizerDetails.logo} alt="Organizer" className="organizer-logo" />
              )}
              <div>
                <p><strong>{event.organizerDetails?.organization || event.organizerDetails?.name}</strong></p>
                <p>Email: {event.organizerDetails?.email}</p>
                <p>Phone: {event.organizerDetails?.phone}</p>
              </div>
            </div>
          </div>

          {/* Registration Section */}
          <div className="registration-section">
            {isRegistered ? (
              <div className="registered-badge">
                ✓ You are registered for this event
              </div>
            ) : (
              <>
                <button
                  className="register-btn"
                  onClick={handleRegister}
                  disabled={registering}
                >
                  {registering ? 'Registering...' : 'Register for Event'}
                </button>

                <a
                  href={event.registrationLink}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="external-link-btn"
                >
                  {event.sector === 'industry'
                    ? 'Visit Career Page'
                    : event.sector === 'government'
                    ? 'Visit Official Portal'
                    : 'Official Link'}
                </a>
              </>
            )}
          </div>

          {error && <div className="error-message">{error}</div>}
        </div>
      </div>
    </div>
  );
};

export default EventDetail;
