import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { eventAPI } from '../services/api';
import { useAuth } from '../context/AuthContext';
import { getImageUrl, handleImageError } from '../utils/imageUtils';
import '../styles/EventDetail.css';

export const EventDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { isAuthenticated } = useAuth(); // 'user' ippo thevai illai registration thookunathala
  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [syncing, setSyncing] = useState(false);

  useEffect(() => {
    fetchEvent();
  }, [id]);

  const fetchEvent = async () => {
    try {
      const response = await eventAPI.getEventById(id);
      setEvent(response.data.event);
    } catch (err) {
      setError('Event not found');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div className="loading">Loading event details...</div>;
  if (!event) return <div className="error-message">Event not found</div>;

  return (
    <div className="event-detail">
      <button className="back-btn" onClick={() => navigate(-1)}>‚Üê Back</button>

      <div className="event-detail-container">
        {event.posterUrl && (
          <div className="event-poster-large">
            <img
              src={getImageUrl(event.posterUrl)}
              alt={event.title}
              onError={handleImageError}
            />
          </div>
        )}

        <div className="event-info">
          <span className={`event-sector-badge sector-${event.sector}`}>
            {event.sector.toUpperCase()}
          </span>

          <h1>{event.title}</h1>

          <div className="event-meta">
            <div className="meta-item">
              <strong>Date:</strong>
              <p>{new Date(event.eventDate).toLocaleDateString()}</p>
            </div>
            <div className="meta-item">
              <strong>Time:</strong>
              <p>{event.eventTime || 'TBD'}</p>
            </div>
            <div className="meta-item">
              <strong>Location:</strong>
              <p>
                {event.location?.address}, {event.location?.city}, {event.location?.state}
              </p>
            </div>
          </div>

          <div className="event-description">
            <h3>Event Details</h3>
            <p>{event.description}</p>
          </div>

          {/* Organizer Section */}
          <div className="organizer-section">
            <h3>Organizer</h3>
            <div className="organizer-info">
              {event.organizerDetails?.logo && (
                <img src={event.organizerDetails.logo} alt="Organizer" className="organizer-logo" />
              )}
              <div>
                <p><strong>{event.organizerDetails?.organization || event.organizerDetails?.name}</strong></p>
                <p>Email: {event.organizerDetails?.email}</p>
                <p>Phone: {event.organizerDetails?.phone}</p>
              </div>
            </div>
          </div>

          {/* Registration Section Modified - Only Official Link Button */}
          <div className="registration-section">
            <a
              href={event.registrationLink}
              target="_blank"
              rel="noopener noreferrer"
              className="external-link-btn"
              style={{ 
                display: 'block', 
                width: '100%', 
                textAlign: 'center', 
                padding: '15px', 
                fontSize: '1.2rem',
                backgroundColor: '#2c3e50',
                color: 'white',
                textDecoration: 'none',
                borderRadius: '8px',
                fontWeight: 'bold'
              }}
            >
              {event.sector === 'industry'
                ? 'Visit Career Page'
                : event.sector === 'government'
                ? 'Visit Official Portal'
                : 'Official Link'}
            </a>
          </div>

          {error && <div className="error-message">{error}</div>}
        </div>
      </div>
    </div>
  );
};

export default EventDetail;

// Event Details page logic
const handleCalendarSync = async () => {
  try {
    // Backend API call to sync this specific event
    await eventAPI.registerForEvent(event._id); 
    
    // Notification logic
    alert(`üìÖ Success! ${event.title} is now scheduled in your Google Calendar.`);
  } catch (err) {
    console.error("Sync error:", err);
    alert("Cant sync! Please ensure your Google account is linked and try again.");
  }
};

// Return block-la Official Link pakkathula
<div className="event-actions">
  <a href={event.officialLink} className="official-link-btn">Official Link</a>
  
  <button onClick={handleCalendarSync} className="sync-calendar-btn">
    üìÖ Add to Calendar
  </button>
</div>
